// src/components/AnalysisResults.js
import React, { useState, useEffect } from "react";
import { BarChart2, Leaf, Clock } from "lucide-react";

export function AnalysisResults({
  analysis,
  isAnalyzing,
  weather,
  location,
  aiSoilType,
  aiConfidence,
}) {
  const [activeCropIndex, setActiveCropIndex] = useState(0);

  useEffect(() => {
    // reset selected crop when new analysis comes in
    setActiveCropIndex(0);
  }, [analysis]);

  if (!analysis) {
    return (
      <div className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100/70 flex flex-col justify-center items-center text-center">
        <BarChart2 className="w-10 h-10 text-emerald-500 mb-4" />
        {isAnalyzing ? (
          <>
            <p className="text-green-900 font-semibold mb-1">
              Analyzing your soil image…
            </p>
            <p className="text-green-700 text-sm">
              We’re detecting soil type and generating crop recommendations.
            </p>
          </>
        ) : (
          <>
            <p className="text-green-900 font-semibold mb-1">
              No analysis yet
            </p>
            <p className="text-green-700 text-sm">
              Upload a soil image on the left to view AI-powered insights and
              crop suggestions here.
            </p>
          </>
        )}
      </div>
    );
  }

  const {
    soilType,
    pH,
    nutrients,
    moisture,
    texture,
    weatherAdjustment,
    locationAdjustment,
    recommendedCrops,
  } = analysis;

  const selectedCrop =
    recommendedCrops && recommendedCrops.length > 0
      ? recommendedCrops[Math.min(activeCropIndex, recommendedCrops.length - 1)]
      : null;

  const displaySoilLabel = aiSoilType ? `${soilType}` : soilType;
  const displayConfidence =
    aiConfidence != null ? `${(aiConfidence * 100).toFixed(1)}%` : null;

  return (
    <div className="flex flex-col gap-8 max-h-[calc(100vh-260px)] lg:max-h-[calc(100vh-220px)] overflow-y-auto pr-2">
      {/* ------------ SECTION 1: ANALYSIS SUMMARY ------------ */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100/70">
        <div className="flex items-center gap-3 mb-5">
          <div className="w-9 h-9 rounded-2xl bg-gradient-to-br from-green-500 to-emerald-600 flex items-center justify-center">
            <BarChart2 className="w-5 h-5 text-white" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Analysis Results
            </h2>
            <p className="text-xs text-green-700">AI-powered insights</p>
          </div>
        </div>

        {/* Weather/location narrative */}
        <div className="grid gap-4 mb-6">
          {weatherAdjustment && (
            <div className="bg-amber-50 border border-amber-100 rounded-2xl px-4 py-3 text-xs text-amber-900">
              <p>{weatherAdjustment}</p>
            </div>
          )}
          {locationAdjustment && (
            <div className="bg-emerald-50/60 border border-emerald-100 rounded-2xl px-4 py-3 text-xs text-emerald-900">
              <p>{locationAdjustment}</p>
            </div>
          )}
        </div>

        {/* Soil + environment grid */}
        <div className="grid md:grid-cols-2 gap-5">
          {/* Soil type, pH, moisture */}
          <div className="space-y-4">
            <div className="bg-emerald-50/70 rounded-2xl p-4">
              <p className="text-[11px] text-green-700 mb-1">Soil Type</p>
              <p className="text-sm font-semibold text-green-900">
                {displaySoilLabel}
              </p>
              <p className="text-[11px] text-green-700 mt-1">{texture}</p>
              {displayConfidence && (
                <p className="mt-2 text-[11px] text-emerald-700">
                  Model confidence:{" "}
                  <span className="font-semibold">{displayConfidence}</span>
                </p>
              )}
            </div>

            <div className="grid grid-cols-2 gap-3">
              <div className="bg-emerald-50/70 rounded-2xl p-3">
                <p className="text-[11px] text-green-700 mb-1">pH Level</p>
                <div className="inline-flex items-center gap-2">
                  <span className="inline-flex items-center justify-center px-2 py-1 rounded-full bg-white text-[11px] font-semibold text-green-900 shadow-sm">
                    {pH}
                  </span>
                  <span className="text-[11px] text-green-700">
                    {pH < 6.5
                      ? "Slightly acidic"
                      : pH > 7.5
                      ? "Alkaline"
                      : "Near neutral"}
                  </span>
                </div>
              </div>

              <div className="bg-emerald-50/70 rounded-2xl p-3">
                <p className="text-[11px] text-green-700 mb-1">Moisture</p>
                <p className="text-sm font-semibold text-green-900">
                  {moisture}
                </p>
              </div>
            </div>
          </div>

          {/* Environment & nutrients */}
          <div className="space-y-4">
            <div className="bg-emerald-50/70 rounded-2xl p-4">
              <p className="text-[11px] text-green-700 mb-2">
                Environment snapshot
              </p>
              <ul className="space-y-1 text-[11px] text-green-800">
                {location && (
                  <li>
                    <span className="font-semibold">Location:</span>{" "}
                    {location.city}, {location.country}
                  </li>
                )}
                {weather && (
                  <>
                    <li>
                      <span className="font-semibold">Temperature:</span>{" "}
                      {weather.temperature}°C, {weather.condition}
                    </li>
                    <li>
                      <span className="font-semibold">Humidity:</span>{" "}
                      {weather.humidity}% &nbsp;·&nbsp;
                      <span className="font-semibold">Rainfall:</span>{" "}
                      {weather.rainfall} mm
                    </li>
                    <li>
                      <span className="font-semibold">Season:</span>{" "}
                      {weather.season}
                    </li>
                  </>
                )}
              </ul>
            </div>

            <div className="bg-emerald-50/70 rounded-2xl p-4">
              <p className="text-[11px] text-green-700 mb-2">
                Nutrient Levels (NPK)
              </p>
              <div className="grid grid-cols-3 gap-2 text-center">
                {["nitrogen", "phosphorus", "potassium"].map((key) => (
                  <div
                    key={key}
                    className="bg-white rounded-xl py-2 text-[11px] shadow-sm"
                  >
                    <p className="text-[10px] uppercase tracking-wide text-green-700">
                      {key === "nitrogen"
                        ? "Nitrogen"
                        : key === "phosphorus"
                        ? "Phosphorus"
                        : "Potassium"}
                    </p>
                    <p className="font-semibold text-green-900 mt-1">
                      {nutrients[key]}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* ------------ SECTION 2: RECOMMENDED CROPS ------------ */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100/70">
        <div className="flex items-center gap-3 mb-5">
          <div className="w-9 h-9 rounded-2xl bg-gradient-to-br from-emerald-500 to-green-500 flex items-center justify-center">
            <Leaf className="w-5 h-5 text-white" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Recommended Crops
            </h2>
            <p className="text-xs text-green-700">
              Ranked by suitability for your soil and conditions.
            </p>
          </div>
        </div>

        {(!recommendedCrops || recommendedCrops.length === 0) && (
          <p className="text-sm text-green-700">
            No crop recommendations available for this soil profile.
          </p>
        )}

        <div className="space-y-4">
          {recommendedCrops.map((crop, index) => (
            <button
              key={crop.name}
              type="button"
              onClick={() => setActiveCropIndex(index)}
              className={`w-full text-left rounded-2xl border transition shadow-sm ${
                index === activeCropIndex
                  ? "border-emerald-300 bg-emerald-50/70"
                  : "border-green-100 hover:border-emerald-200 hover:bg-emerald-50/40"
              }`}
            >
              <div className="p-4 flex flex-col gap-2">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <span className="inline-flex w-8 h-8 rounded-full bg-emerald-500/10 items-center justify-center text-emerald-700 text-sm">
                      <Leaf className="w-4 h-4" />
                    </span>
                    <div>
                      <p className="text-sm font-semibold text-green-900">
                        {crop.name}
                      </p>
                      <p className="text-[11px] text-green-700">
                        {crop.reason}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <span className="inline-flex items-center px-2.5 py-1 rounded-full bg-emerald-500 text-[10px] font-semibold text-white">
                      {crop.suitability}% Match
                    </span>
                  </div>
                </div>

                {/* progress bar */}
                <div className="mt-1">
                  <div className="h-1.5 rounded-full bg-emerald-100 overflow-hidden">
                    <div
                      className="h-1.5 bg-emerald-500 rounded-full"
                      style={{ width: `${crop.suitability}%` }}
                    />
                  </div>
                  <p className="mt-1 text-[11px] text-green-700 flex items-center gap-1">
                    <span className="inline-block w-1.5 h-1.5 rounded-full bg-emerald-500" />
                    {crop.weatherImpact || "Weather conditions are suitable"}
                  </p>
                </div>
              </div>
            </button>
          ))}
        </div>
      </section>

      {/* ------------ SECTION 3: GROWTH TIMELINE ------------ */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100/70">
        <div className="flex items-center gap-3 mb-5">
          <div className="w-9 h-9 rounded-2xl bg-gradient-to-br from-emerald-500 to-green-500 flex items-center justify-center">
            <Clock className="w-5 h-5 text-white" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Growth Timeline
            </h2>
            <p className="text-xs text-green-700">
              Step-by-step guide for planting and harvesting.
            </p>
          </div>
        </div>

        {selectedCrop ? (
          <>
            {/* tabs for crops */}
            <div className="flex flex-wrap gap-2 mb-5">
              {recommendedCrops.map((crop, index) => (
                <button
                  key={crop.name}
                  type="button"
                  onClick={() => setActiveCropIndex(index)}
                  className={`px-3 py-1.5 rounded-full text-[11px] border transition ${
                    index === activeCropIndex
                      ? "bg-emerald-500 text-white border-emerald-500"
                      : "bg-emerald-50/70 text-green-800 border-emerald-200 hover:bg-emerald-100"
                  }`}
                >
                  {crop.name}
                </button>
              ))}
            </div>

            {/* vertical timeline */}
            <div className="relative pl-5">
              <div className="absolute left-2 top-0 bottom-0 w-[2px] bg-emerald-200" />
              <div className="space-y-4">
                {selectedCrop.growthTimeline.map((step, idx) => (
                  <div key={idx} className="relative flex gap-3">
                    <div className="absolute -left-0.5 mt-1 w-3 h-3 rounded-full bg-emerald-500 border-2 border-white shadow" />
                    <div className="bg-emerald-50/80 rounded-2xl px-4 py-3 flex-1">
                      <div className="flex items-center justify-between mb-1">
                        <p className="text-[12px] font-semibold text-green-900">
                          {step.stage}
                        </p>
                        <span className="inline-flex items-center px-2 py-0.5 rounded-full bg-white text-[10px] text-green-800 border border-emerald-100">
                          {step.duration}
                        </span>
                      </div>
                      <p className="text-[11px] text-green-800">
                        {step.description}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </>
        ) : (
          <p className="text-sm text-green-700">
            Select a crop from the list above to view its detailed growth
            timeline.
          </p>
        )}
      </section>
    </div>
  );
}
