// src/components/AnalysisResults.js
import React, { useState, useEffect } from "react";
import { BarChart3, Leaf, Droplets, ThermometerSun, Clock } from "lucide-react";

export function AnalysisResults({
  analysis,
  isAnalyzing,
  weather,
  location,
  aiSoilType,
  aiConfidence,
}) {
  const [activeCropIndex, setActiveCropIndex] = useState(0);

  // when a new analysis comes in, reset to first crop
  useEffect(() => {
    if (analysis && analysis.recommendedCrops?.length) {
      setActiveCropIndex(0);
    }
  }, [analysis]);

  // ---------- EMPTY / LOADING STATES ----------
  if (!analysis && !isAnalyzing) {
    return (
      <div className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 flex flex-col items-center justify-center text-center h-full">
        <BarChart3 className="w-10 h-10 text-emerald-500 mb-4" />
        <h2 className="text-xl font-semibold text-green-900 mb-2">
          Analysis Results
        </h2>
        <p className="text-green-700 text-sm max-w-sm">
          Upload a clear image of your soil to see AI-powered analysis,
          recommended crops, and a detailed growth timeline.
        </p>
      </div>
    );
  }

  if (!analysis && isAnalyzing) {
    return (
      <div className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 flex flex-col items-center justify-center text-center h-full">
        <div className="w-10 h-10 rounded-full border-4 border-emerald-200 border-t-emerald-500 animate-spin mb-4" />
        <h2 className="text-xl font-semibold text-green-900 mb-2">
          Analyzing Soil…
        </h2>
        <p className="text-green-700 text-sm max-w-sm">
          Your image is being processed by the AI model and combined with local
          weather and climate data.
        </p>
      </div>
    );
  }

  if (!analysis) return null; // safety

  const crops = analysis.recommendedCrops || [];
  const activeCrop = crops[activeCropIndex];

  // small helper for NPK badge color
  const levelColor = (level) => {
    if (!level) return "bg-gray-100 text-gray-600";
    const lvl = level.toLowerCase();
    if (lvl === "high") return "bg-rose-50 text-rose-700";
    if (lvl === "medium") return "bg-amber-50 text-amber-700";
    return "bg-sky-50 text-sky-700";
  };

  // ---------- MAIN LAYOUT ----------
  return (
    <div className="space-y-8">
      {/* ---------------- ANALYSIS RESULTS CARD (matches initial design) ---------------- */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 space-y-6">
        {/* header */}
        <div className="flex items-start gap-3">
          <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-emerald-500 to-green-600 flex items-center justify-center">
            <BarChart3 className="w-5 h-5 text-white" />
          </div>
          <div>
            <h2 className="text-xl font-semibold text-green-900">
              Analysis Results
            </h2>
            <p className="text-green-700 text-sm">AI-powered insights</p>
          </div>
        </div>

        {/* weather + location messages – yellow + light green banners like initial */}
        {(analysis.weatherAdjustment || analysis.locationAdjustment) && (
          <div className="space-y-3">
            {analysis.weatherAdjustment && (
              <div className="flex gap-3 rounded-2xl bg-amber-50 border border-amber-100 px-4 py-3 text-sm text-amber-900">
                <ThermometerSun className="w-4 h-4 mt-[2px] flex-shrink-0" />
                <p>{analysis.weatherAdjustment}</p>
              </div>
            )}
            {analysis.locationAdjustment && (
              <div className="flex gap-3 rounded-2xl bg-emerald-50/70 border border-emerald-100 px-4 py-3 text-sm text-emerald-900">
                <Clock className="w-4 h-4 mt-[2px] flex-shrink-0" />
                <p>{analysis.locationAdjustment}</p>
              </div>
            )}
          </div>
        )}

        {/* soil type + pH & moisture, same hierarchy as initial screenshot */}
        <div className="grid gap-4 md:grid-cols-2">
          {/* soil type card */}
          <div className="rounded-2xl bg-emerald-50/70 border border-emerald-100 px-4 py-4 space-y-2">
            <div className="flex items-center gap-2 text-sm font-medium text-green-900">
              <Leaf className="w-4 h-4" />
              <span>Soil Type</span>
            </div>
            <p className="text-green-900 font-semibold">
              {analysis.soilType}
              {aiSoilType && (
                <span className="ml-1 text-xs text-green-700 font-normal">
                  (model: {aiSoilType})
                </span>
              )}
            </p>
            <p className="text-green-700 text-sm">{analysis.texture}</p>
          </div>

          {/* pH & moisture */}
          <div className="grid gap-4 sm:grid-cols-2">
            <div className="rounded-2xl border border-green-100 px-4 py-4 space-y-2">
              <p className="text-sm font-medium text-green-900">pH Level</p>
              <div className="inline-flex items-center gap-2">
                <span className="inline-flex items-center justify-center px-3 py-1 rounded-full bg-emerald-500 text-white text-sm font-semibold">
                  {analysis.pH}
                </span>
                <span className="text-xs text-green-700">Near neutral</span>
              </div>
            </div>

            <div className="rounded-2xl border border-green-100 px-4 py-4 space-y-2">
              <p className="text-sm font-medium text-green-900 flex items-center gap-2">
                <Droplets className="w-4 h-4 text-emerald-500" />
                <span>Moisture</span>
              </p>
              <p className="text-green-800 text-sm">{analysis.moisture}</p>
              {weather && location && (
                <p className="text-xs text-green-700">
                  Environment: {location.city}, {location.country} •{" "}
                  {weather.temperature}°C • {weather.humidity}% humidity
                </p>
              )}
            </div>
          </div>
        </div>

        {/* NPK levels */}
        <div className="mt-2">
          <p className="text-sm font-medium text-green-900 mb-3">
            Nutrient Levels (NPK)
          </p>
          <div className="flex flex-wrap gap-3">
            <div
              className={`px-4 py-2 rounded-xl text-xs font-medium ${levelColor(
                analysis.nutrients?.nitrogen
              )}`}
            >
              <p className="text-[11px] uppercase tracking-wide mb-0.5">
                Nitrogen
              </p>
              <p className="text-xs">{analysis.nutrients?.nitrogen}</p>
            </div>
            <div
              className={`px-4 py-2 rounded-xl text-xs font-medium ${levelColor(
                analysis.nutrients?.phosphorus
              )}`}
            >
              <p className="text-[11px] uppercase tracking-wide mb-0.5">
                Phosphorus
              </p>
              <p className="text-xs">{analysis.nutrients?.phosphorus}</p>
            </div>
            <div
              className={`px-4 py-2 rounded-xl text-xs font-medium ${levelColor(
                analysis.nutrients?.potassium
              )}`}
            >
              <p className="text-[11px] uppercase tracking-wide mb-0.5">
                Potassium
              </p>
              <p className="text-xs">{analysis.nutrients?.potassium}</p>
            </div>
            {aiConfidence != null && (
              <div className="px-4 py-2 rounded-xl bg-slate-50 border border-slate-100 text-xs text-slate-700">
                Model confidence:{" "}
                <span className="font-semibold">
                  {(aiConfidence * 100).toFixed(1)}%
                </span>
              </div>
            )}
          </div>
        </div>
      </section>

      {/* ---------------- RECOMMENDED CROPS (styled like your initial 3-column card) ---------------- */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 space-y-4">
        <div className="flex items-start gap-3 mb-2">
          <div className="w-9 h-9 rounded-xl bg-emerald-50 flex items-center justify-center">
            <Leaf className="w-4 h-4 text-emerald-600" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Recommended Crops
            </h2>
            <p className="text-green-700 text-sm">
              Ranked by suitability for your soil and current conditions.
            </p>
          </div>
        </div>

        <div className="space-y-4">
          {crops.map((crop) => (
            <div
              key={crop.name}
              className="rounded-2xl border border-green-100 bg-emerald-50/40 px-5 py-4"
            >
              {/* top row : name + match area like initial design */}
              <div className="flex items-start justify-between gap-4 mb-2">
                <div className="flex items-center gap-2">
                  <Leaf className="w-4 h-4 text-emerald-600" />
                  <div className="text-sm text-green-900 font-semibold">
                    {crop.name}
                  </div>
                </div>

                <div className="text-right text-xs text-green-800">
                  <div className="font-semibold">Match</div>
                  <div>{Math.round(crop.suitability)}% Match</div>
                  <div className="mt-1 text-[11px] text-green-700">
                    {crop.weatherImpact}
                  </div>
                </div>
              </div>

              {/* description under, like the sample “Loamy soil holds nutrients…” */}
              <p className="text-green-800 text-sm">{crop.reason}</p>
            </div>
          ))}

          {crops.length === 0 && (
            <p className="text-sm text-green-700">
              No crop recommendations available for this soil type.
            </p>
          )}
        </div>
      </section>

      {/* ---------------- GROWTH TIMELINE (tabs + stacked rectangular steps) ---------------- */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 space-y-6">
        <div className="flex items-start gap-3">
          <div className="w-9 h-9 rounded-xl bg-emerald-50 flex items-center justify-center">
            <Clock className="w-4 h-4 text-emerald-600" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Growth Timeline
            </h2>
            <p className="text-green-700 text-sm">
              Step-by-step guide for planting and harvesting in your soil type.
            </p>
          </div>
        </div>

        {/* crop tabs – simple white rectangles with border like initial UI */}
        <div className="flex flex-wrap gap-2">
          {crops.map((crop, index) => {
            const isActive = index === activeCropIndex;
            return (
              <button
                key={crop.name}
                type="button"
                onClick={() => setActiveCropIndex(index)}
                className={`px-4 py-1.5 text-xs font-medium rounded border ${
                  isActive
                    ? "bg-emerald-500 text-white border-emerald-500"
                    : "bg-white text-emerald-800 border-emerald-200 hover:bg-emerald-50"
                }`}
              >
                {crop.name}
              </button>
            );
          })}
        </div>

        {/* timeline body – stacked rounded rectangles (no vertical line/dots) */}
        {activeCrop ? (
          <div className="mt-4">
            <p className="text-sm text-green-800 mb-4">
              Follow this timeline for{" "}
              <span className="font-semibold">{activeCrop.name}</span> in{" "}
              <span className="font-semibold">{analysis.soilType}</span>.
            </p>

            <div className="space-y-3">
              {activeCrop.growthTimeline.map((step, index) => (
                <div
                  key={`${step.stage}-${index}`}
                  className="rounded-2xl border border-emerald-200 bg-emerald-50/60 px-4 py-3"
                >
                  <div className="flex items-center justify-between gap-3 mb-1">
                    <p className="text-sm font-semibold text-green-900">
                      {step.stage}
                    </p>
                    {step.duration && (
                      <span className="px-2.5 py-1 rounded-full bg-white border border-emerald-100 text-[11px] font-medium text-emerald-800">
                        {step.duration}
                      </span>
                    )}
                  </div>
                  <p className="text-xs text-green-800 leading-relaxed">
                    {step.description}
                  </p>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <p className="text-sm text-green-700">
            No growth timeline available. Try analyzing another soil image.
          </p>
        )}
      </section>
    </div>
  );
}
