// src/components/AnalysisResults.js
import React, { useState } from "react";
import { BarChart3, Droplets, FlaskConical, Leaf } from "lucide-react";

export function AnalysisResults({
  analysis,
  isAnalyzing,
  weather,
  location,
  aiSoilType,
  aiConfidence,
}) {
  // which crop is selected in the Growth Timeline tabs
  const [activeCropIndex, setActiveCropIndex] = useState(0);

  // easy handles
  const hasAnalysis = !!analysis;
  const crops = analysis?.recommendedCrops || [];
  const activeCrop = crops[activeCropIndex] || null;

  const environmentSummary =
    weather && location
      ? `${location.city}, ${location.country} ‚Ä¢ ${weather.temperature}¬∞C ‚Ä¢ ${weather.humidity}% humidity`
      : null;

  if (!hasAnalysis && !isAnalyzing) {
    // initial empty state
    return (
      <div className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100/70 flex flex-col justify-center items-center text-center">
        <BarChart3 className="w-10 h-10 text-emerald-500 mb-4" />
        <h2 className="text-xl font-semibold text-green-900 mb-2">
          Analysis Results
        </h2>
        <p className="text-green-700 text-sm max-w-sm">
          Upload a soil image on the left to view AI-powered soil analysis,
          recommended crops, and a step-by-step growth timeline.
        </p>
      </div>
    );
  }

  if (isAnalyzing) {
    // loading state
    return (
      <div className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100/70">
        <div className="flex items-center gap-3 mb-4">
          <BarChart3 className="w-6 h-6 text-emerald-500 animate-pulse" />
          <h2 className="text-xl font-semibold text-green-900">
            Analyzing Soil‚Ä¶
          </h2>
        </div>
        <p className="text-green-700 text-sm">
          We‚Äôre reading your soil image and combining it with your weather and
          location data to generate detailed recommendations.
        </p>

        <div className="mt-6 space-y-3">
          <SkeletonLine />
          <SkeletonLine width="80%" />
          <SkeletonLine width="60%" />
        </div>
      </div>
    );
  }

  // ---------- MAIN RENDER ----------
  return (
    <div className="space-y-6">
      {/* ANALYSIS RESULTS CARD */}
      <div className="bg-white rounded-3xl p-7 shadow-lg shadow-green-500/5 border border-green-100/70">
        {/* Header */}
        <div className="flex items-center gap-3 mb-5">
          <div className="w-9 h-9 rounded-xl bg-gradient-to-br from-emerald-500 to-green-600 flex items-center justify-center">
            <BarChart3 className="w-5 h-5 text-white" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Analysis Results
            </h2>
            <p className="text-xs text-green-700">AI-powered insights</p>
          </div>
        </div>

        {/* Weather + location banners */}
        <div className="space-y-3 mb-5">
          {analysis.weatherAdjustment && (
            <div className="bg-amber-50 border border-amber-100 rounded-2xl px-4 py-3 text-xs text-amber-900 flex gap-2">
              <span className="mt-0.5 text-[10px]">‚ö†Ô∏è</span>
              <p>{analysis.weatherAdjustment}</p>
            </div>
          )}
          {analysis.locationAdjustment && (
            <div className="bg-emerald-50/70 border border-emerald-100 rounded-2xl px-4 py-3 text-xs text-emerald-900 flex gap-2">
              <span className="mt-0.5 text-[10px]">üìç</span>
              <p>{analysis.locationAdjustment}</p>
            </div>
          )}
        </div>

        {/* Soil type block */}
        <div className="bg-emerald-50/70 border border-emerald-100 rounded-2xl px-5 py-4 mb-5">
          <div className="flex items-center gap-2 mb-2">
            <div className="w-7 h-7 rounded-full bg-white flex items-center justify-center border border-emerald-100">
              <Leaf className="w-4 h-4 text-emerald-500" />
            </div>
            <p className="text-xs font-medium text-emerald-800 uppercase tracking-wide">
              Soil Type
            </p>
          </div>
          <p className="text-sm font-semibold text-emerald-900">
            {analysis.soilType}
          </p>
          <p className="text-xs text-emerald-800 mt-1">{analysis.texture}</p>
          {aiSoilType && (
            <p className="text-[11px] text-emerald-700 mt-2">
              Model prediction:{" "}
              <span className="font-semibold">{aiSoilType}</span>
              {aiConfidence != null &&
                ` ‚Ä¢ ${(aiConfidence * 100).toFixed(1)}% confidence`}
            </p>
          )}
        </div>

        {/* pH + Moisture */}
        <div className="grid grid-cols-2 gap-4 mb-5">
          <div className="bg-emerald-50/40 rounded-2xl px-4 py-3 border border-emerald-100">
            <p className="text-xs font-medium text-emerald-800 uppercase tracking-wide mb-1">
              pH Level
            </p>
            <div className="flex items-baseline gap-2">
              <span className="px-3 py-1 rounded-xl bg-white shadow-sm text-emerald-900 text-sm font-semibold">
                {analysis.pH}
              </span>
              <span className="text-xs text-emerald-700">Near neutral</span>
            </div>
          </div>

          <div className="bg-emerald-50/40 rounded-2xl px-4 py-3 border border-emerald-100">
            <div className="flex items-center gap-2 mb-1">
              <Droplets className="w-4 h-4 text-emerald-500" />
              <p className="text-xs font-medium text-emerald-800 uppercase tracking-wide">
                Moisture
              </p>
            </div>
            <p className="text-sm font-semibold text-emerald-900">
              {analysis.moisture}
            </p>
            {environmentSummary && (
              <p className="text-[11px] text-emerald-700 mt-1">
                Environment: {environmentSummary}
              </p>
            )}
          </div>
        </div>

        {/* NPK chips */}
        <div className="bg-emerald-50/30 rounded-2xl px-4 py-3 border border-emerald-100">
          <div className="flex items-center gap-2 mb-2">
            <FlaskConical className="w-4 h-4 text-emerald-500" />
            <p className="text-xs font-medium text-emerald-800 uppercase tracking-wide">
              Nutrient Levels (NPK)
            </p>
          </div>

          <div className="flex flex-wrap gap-3 text-xs">
            <NutrientChip name="Nitrogen" level={analysis.nutrients.nitrogen} />
            <NutrientChip
              name="Phosphorus"
              level={analysis.nutrients.phosphorus}
            />
            <NutrientChip
              name="Potassium"
              level={analysis.nutrients.potassium}
            />
          </div>
        </div>
      </div>

      {/* RECOMMENDED CROPS */}
      <div className="bg-white rounded-3xl p-7 shadow-lg shadow-green-500/5 border border-green-100/70">
        <div className="flex items-center gap-3 mb-5">
          <div className="w-8 h-8 rounded-xl bg-emerald-50 flex items-center justify-center border border-emerald-100">
            <Leaf className="w-4 h-4 text-emerald-600" />
          </div>
          <div>
            <h3 className="text-lg font-semibold text-green-900">
              Recommended Crops
            </h3>
            <p className="text-xs text-green-700">
              Ranked by suitability for your soil and current conditions.
            </p>
          </div>
        </div>

        <div className="space-y-4">
          {crops.map((crop, idx) => (
            <div
              key={crop.name}
              className="bg-emerald-50/60 border border-emerald-100 rounded-2xl p-4 cursor-pointer transition hover:bg-emerald-50"
              onClick={() => setActiveCropIndex(idx)}
            >
              <div className="flex items-center justify-between gap-3 mb-2">
                <div className="flex items-center gap-2">
                  <div className="w-7 h-7 rounded-full bg-white flex items-center justify-center border border-emerald-100">
                    <Leaf className="w-4 h-4 text-emerald-500" />
                  </div>
                  <div>
                    <p className="text-sm font-semibold text-emerald-900">
                      {crop.name}
                    </p>
                    <p className="text-[11px] text-emerald-700">
                      {crop.reason}
                    </p>
                  </div>
                </div>

                <div className="text-right">
                  <span className="inline-flex items-center px-2 py-1 rounded-full bg-emerald-100 text-[11px] font-semibold text-emerald-800">
                    {Math.round(crop.suitability)}% Match
                  </span>
                  <p className="text-[11px] text-emerald-700 mt-1">
                    {crop.weatherImpact}
                  </p>
                </div>
              </div>

              {/* progress bar */}
              <div className="h-2 rounded-full bg-emerald-100 overflow-hidden">
                <div
                  className="h-full bg-emerald-500 rounded-full"
                  style={{ width: `${Math.round(crop.suitability)}%` }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* GROWTH TIMELINE */}
      {activeCrop && (
        <div className="bg-white rounded-3xl p-7 shadow-lg shadow-green-500/5 border border-green-100/70">
          <div className="flex items-center gap-3 mb-5">
            <div className="w-8 h-8 rounded-xl bg-emerald-50 flex items-center justify-center border border-emerald-100">
              <BarChart3 className="w-4 h-4 text-emerald-600" />
            </div>
            <div>
              <h3 className="text-lg font-semibold text-green-900">
                Growth Timeline
              </h3>
              <p className="text-xs text-green-700">
                Step-by-step guide for planting and harvesting in your soil
                type.
              </p>
            </div>
          </div>

          {/* crop tabs */}
          <div className="flex flex-wrap gap-2 mb-5 text-xs">
            {crops.map((crop, idx) => (
              <button
                key={crop.name}
                type="button"
                onClick={() => setActiveCropIndex(idx)}
                className={`px-3 py-1 rounded-full border text-xs transition ${
                  idx === activeCropIndex
                    ? "bg-emerald-500 text-white border-emerald-500"
                    : "bg-emerald-50 text-emerald-800 border-emerald-100 hover:bg-emerald-100"
                }`}
              >
                {crop.name}
              </button>
            ))}
          </div>

          <p className="text-xs text-emerald-700 mb-4">
            Follow this timeline for <span className="font-semibold">{activeCrop.name}</span> in{" "}
            <span className="font-semibold">{analysis.soilType}</span>.
          </p>

          {/* vertical timeline */}
          <div className="relative mt-2">
            {activeCrop.growthTimeline.map((step, idx) => (
              <div
                key={`${step.stage}-${idx}`}
                className="relative pl-9 pb-6 last:pb-0"
              >
                {/* vertical line */}
                {idx !== activeCrop.growthTimeline.length - 1 && (
                  <div className="absolute left-3.5 top-4 bottom-0 w-0.5 bg-emerald-100" />
                )}

                {/* dot */}
                <div className="absolute left-1 top-3 w-5 h-5 rounded-full border-2 border-emerald-500 bg-white flex items-center justify-center">
                  <div className="w-2 h-2 rounded-full bg-emerald-500" />
                </div>

                <div className="flex items-center justify-between gap-3 mb-1">
                  <p className="text-sm font-semibold text-emerald-900">
                    {step.stage}
                  </p>
                  <span className="px-2 py-1 rounded-full bg-emerald-50 border border-emerald-100 text-[11px] text-emerald-800">
                    {step.duration}
                  </span>
                </div>
                <p className="text-xs text-emerald-700">{step.description}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

/* ---------- Small helper components ---------- */

function NutrientChip({ name, level }) {
  return (
    <div className="flex items-center gap-2">
      <span className="px-2 py-1 rounded-lg bg-white border border-emerald-100 text-[11px] font-medium text-emerald-900">
        {name}
      </span>
      <span className="px-2 py-1 rounded-lg bg-emerald-100 text-[11px] text-emerald-900">
        {level}
      </span>
    </div>
  );
}

function SkeletonLine({ width = "100%" }) {
  return (
    <div
      className="h-3 rounded-full bg-emerald-100/80 animate-pulse"
      style={{ width }}
    />
  );
}
