// src/components/AnalysisResults.js
import React, { useState, useEffect } from "react";
import { Leaf, Droplets, ThermometerSun, Clock } from "lucide-react";

export function AnalysisResults({ analysis, isAnalyzing, weather, location }) {
  const [tab, setTab] = useState(0);

  useEffect(() => {
    setTab(0);
  }, [analysis]);

  if (!analysis && !isAnalyzing)
    return (
      <div className="rounded-3xl bg-white border p-8 text-center">
        <p className="text-green-700">Upload soil to generate analysis.</p>
      </div>
    );

  if (isAnalyzing)
    return (
      <div className="rounded-3xl bg-white border p-8 text-center">
        <div className="loader mx-auto mb-3"></div>
        <p className="text-green-700">Analyzing soil...</p>
      </div>
    );

  const crop = analysis.recommendedCrops[tab];

  return (
    <div className="space-y-6">
      {/* ========== TOP ANALYSIS PANEL ========== */}
      <div className="rounded-3xl bg-white border p-8 space-y-5">

        <h2 className="text-lg font-bold text-green-900 flex items-center gap-2">
          üìä Analysis Results
        </h2>
        <p className="text-green-700 -mt-3">AI-powered insights</p>

        {/* climate + weather messages */}
        <div className="space-y-3">
          {analysis.weatherAdjustment && (
            <div className="border rounded-xl p-3 flex gap-2 text-green-900">
              <ThermometerSun size={17} />
              {analysis.weatherAdjustment}
            </div>
          )}
          {analysis.locationAdjustment && (
            <div className="border rounded-xl p-3 flex gap-2 text-green-900">
              <Clock size={17} />
              {analysis.locationAdjustment}
            </div>
          )}
        </div>

        {/* Soil block */}
        <div className="border rounded-xl p-4">
          <Leaf className="text-green-600 mb-2" size={18} />
          <p className="font-semibold text-green-900">{analysis.soilType}</p>
          <p className="text-green-800 text-sm">{analysis.texture}</p>
        </div>

        {/* pH */}
        <div className="border rounded-xl p-4">
          <p className="font-semibold text-green-900">pH Level</p>
          <p className="text-green-800">{analysis.pH} <span className="text-green-600">Near neutral</span></p>
        </div>

        {/* Moisture */}
        <div className="border rounded-xl p-4">
          <p className="font-semibold text-green-900 flex gap-1">
            <Droplets size={17} className="text-green-600" /> Moisture
          </p>
          <p className="text-green-800">{analysis.moisture}</p>

          {weather && location && (
            <p className="text-green-700 text-xs mt-1">
              Environment: {location.city}, {location.country} ‚Ä¢ {weather.temperature}¬∞C ‚Ä¢ {weather.humidity}% humidity
            </p>
          )}
        </div>

        {/* NPK */}
        <div>
          <p className="font-semibold text-green-900">Nutrient Levels (NPK)</p>
          <div className="grid grid-cols-3 text-center text-green-900 font-medium mt-2">
            <p>Nitrogen</p><p>Phosphorus</p><p>Potassium</p>
          </div>
          <div className="grid grid-cols-3 text-center text-green-700 text-sm">
            <p>{analysis.nutrients.nitrogen}</p>
            <p>{analysis.nutrients.phosphorus}</p>
            <p>{analysis.nutrients.potassium}</p>
          </div>
        </div>
      </div>

      {/* ========== CROP LIST PANEL ========== */}
      <div className="rounded-3xl bg-white border p-8 space-y-3">
        <h2 className="font-bold text-green-900">üåø Recommended Crops</h2>
        <p className="text-green-700 -mt-1">Ranked by suitability</p>

        {analysis.recommendedCrops.map((c, i) => (
          <div key={i} className="border rounded-xl p-4 cursor-pointer hover:bg-green-50 transition"
            onClick={() => setTab(i)}>
            <div className="flex justify-between">
              <b className="text-green-900">{c.name}</b>
              <span className="text-green-700">{c.suitability}% Match</span>
            </div>
            <p className="text-green-800 text-sm mt-1">{c.reason}</p>
            <p className="text-green-700 text-xs">{c.weatherImpact}</p>
          </div>
        ))}
      </div>

      {/* ========== GROWTH TIMELINE ========== */}
      <div className="rounded-3xl bg-white border p-8 space-y-4">
        <h2 className="font-bold text-green-900">‚è≥ Growth Timeline</h2>

        <div className="flex gap-2 flex-wrap">
          {analysis.recommendedCrops.map((c, i) => (
            <button key={i} className={`border px-3 py-1 rounded
              ${tab === i ? "bg-green-600 text-white" : "hover:bg-green-100"}`}
              onClick={() => setTab(i)}>
              {c.name}
            </button>
          ))}
        </div>

        {crop && (
          <div className="space-y-3">
            {crop.growthTimeline.map((step, idx) => (
              <div key={idx} className="border rounded-xl p-4">
                <div className="flex justify-between text-green-900 font-semibold">
                  <p>{step.stage}</p>
                  <span className="border rounded-full px-2 text-xs">{step.duration}</span>
                </div>
                <p className="text-green-800 text-sm">{step.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
