// src/components/AnalysisResults.js
import React, { useState, useEffect } from "react";
import { BarChart3, Leaf, Droplets, ThermometerSun, Clock } from "lucide-react";

export function AnalysisResults({
  analysis,
  isAnalyzing,
  weather,
  location,
  aiSoilType,
  aiConfidence,
}) {
  const [activeCropIndex, setActiveCropIndex] = useState(0);

  useEffect(() => {
    if (analysis && analysis.recommendedCrops?.length) {
      setActiveCropIndex(0);
    }
  }, [analysis]);

  // ---------- EMPTY / LOADING ----------
  if (!analysis && !isAnalyzing) {
    return (
      <div className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 flex flex-col items-center justify-center text-center h-full">
        <BarChart3 className="w-10 h-10 text-emerald-500 mb-4" />
        <h2 className="text-xl font-semibold text-green-900 mb-2">
          Analysis Results
        </h2>
        <p className="text-green-700 text-sm max-w-sm">
          Upload a clear soil image to see AI-powered analysis, crop
          recommendations, and a detailed growth timeline.
        </p>
      </div>
    );
  }

  if (!analysis && isAnalyzing) {
    return (
      <div className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 flex flex-col items-center justify-center text-center h-full">
        <div className="w-10 h-10 rounded-full border-4 border-emerald-200 border-t-emerald-500 animate-spin mb-4" />
        <h2 className="text-xl font-semibold text-green-900 mb-2">
          Analyzing soil…
        </h2>
        <p className="text-green-700 text-sm max-w-sm">
          Your image is being processed and combined with local weather and
          climate data.
        </p>
      </div>
    );
  }

  if (!analysis) return null;

  const crops = analysis.recommendedCrops || [];
  const activeCrop = crops[activeCropIndex];

  // ---------- ANALYSIS CARD (exact target style) ----------
  return (
    <div className="space-y-8">
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 space-y-6">
        {/* Header */}
        <div className="flex items-start gap-3">
          <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-green-500 to-emerald-600 flex items-center justify-center shadow-lg shadow-green-500/30">
            <BarChart3 className="w-5 h-5 text-white" />
          </div>
          <div>
            <h2 className="text-xl font-semibold text-green-900">
              Analysis Results
            </h2>
            <p className="text-green-700 text-sm">AI-powered insights</p>
          </div>
        </div>

        {/* BIG YELLOW BANNER (weather + location in one card) */}
        {(analysis.weatherAdjustment || analysis.locationAdjustment) && (
          <div className="rounded-3xl bg-amber-50 border border-amber-100 px-6 py-5 space-y-2">
            <div className="inline-flex items-center gap-2 text-amber-900 mb-1">
              <div className="w-8 h-8 rounded-full bg-white/80 flex items-center justify-center shadow-sm">
                <ThermometerSun className="w-4 h-4" />
              </div>
              <span className="text-sm font-medium">Current conditions</span>
            </div>
            {analysis.weatherAdjustment && (
              <p className="text-sm text-amber-900">
                {analysis.weatherAdjustment}
              </p>
            )}
            {analysis.locationAdjustment && (
              <p className="text-sm text-amber-900">
                {analysis.locationAdjustment}
              </p>
            )}
          </div>
        )}

        {/* GREEN “SOIL TYPE” CARD */}
        <div className="rounded-3xl bg-emerald-50 border border-emerald-100 px-6 py-5 space-y-2">
          <div className="flex items-center gap-2 text-sm font-medium text-green-900 mb-1">
            <div className="w-8 h-8 rounded-2xl bg-white/80 flex items-center justify-center shadow-sm">
              <Leaf className="w-4 h-4 text-emerald-600" />
            </div>
            <span>Soil Type</span>
          </div>
          <p className="text-green-900 font-semibold text-base">
            {analysis.soilType}
          </p>
          <p className="text-green-800 text-sm">{analysis.texture}</p>
        </div>

        {/* PH + MOISTURE ROW (two white cards) */}
        <div className="grid gap-4 md:grid-cols-2">
          {/* pH card */}
          <div className="rounded-3xl bg-white border border-green-100 px-6 py-5">
            <p className="text-sm font-medium text-green-900 mb-3">
              pH Level
            </p>
            <div className="flex items-center gap-3">
              <span className="inline-flex items-center justify-center px-4 py-1.5 rounded-full bg-emerald-500 text-white text-sm font-semibold shadow-sm">
                {analysis.pH}
              </span>
              <div className="flex flex-col">
                <span className="inline-flex items-center gap-1 text-xs text-green-700">
                  <span className="w-2 h-2 rounded-full bg-emerald-500" />
                  Neutral
                </span>
              </div>
            </div>
          </div>

          {/* moisture card */}
          <div className="rounded-3xl bg-white border border-green-100 px-6 py-5">
            <p className="text-sm font-medium text-green-900 mb-3 flex items-center gap-2">
              <span className="inline-flex w-7 h-7 rounded-full bg-blue-50 items-center justify-center">
                <Droplets className="w-4 h-4 text-blue-500" />
              </span>
              Moisture
            </p>
            <p className="text-green-900 text-sm mb-1">{analysis.moisture}</p>
            {weather && location && (
              <p className="text-xs text-green-700">
                {location.city}, {location.country} • {weather.temperature}°C •{" "}
                {weather.humidity}% humidity
              </p>
            )}
          </div>
        </div>

        {/* NPK CARD WITH COLORED TILES */}
        <div className="rounded-3xl bg-white border border-green-100 px-6 py-5 space-y-4">
          <p className="text-sm font-medium text-green-900">
            Nutrient Levels (NPK)
          </p>

          <div className="grid gap-4 sm:grid-cols-3">
            <div className="rounded-2xl bg-violet-50 px-4 py-3">
              <p className="text-sm font-medium text-violet-800">Nitrogen</p>
              <span className="inline-flex mt-2 px-3 py-1 rounded-full bg-violet-100 text-violet-700 text-xs font-semibold">
                {analysis.nutrients?.nitrogen}
              </span>
            </div>

            <div className="rounded-2xl bg-amber-50 px-4 py-3">
              <p className="text-sm font-medium text-amber-800">Phosphorus</p>
              <span className="inline-flex mt-2 px-3 py-1 rounded-full bg-amber-100 text-amber-700 text-xs font-semibold">
                {analysis.nutrients?.phosphorus}
              </span>
            </div>

            <div className="rounded-2xl bg-sky-50 px-4 py-3">
              <p className="text-sm font-medium text-sky-800">Potassium</p>
              <span className="inline-flex mt-2 px-3 py-1 rounded-full bg-sky-100 text-sky-700 text-xs font-semibold">
                {analysis.nutrients?.potassium}
              </span>
            </div>
          </div>

          {aiConfidence != null && (
            <p className="text-xs text-slate-600 mt-1">
              Model confidence:{" "}
              <span className="font-semibold">
                {(aiConfidence * 100).toFixed(1)}%
              </span>
            </p>
          )}
        </div>
      </section>

      {/* ---------- RECOMMENDED CROPS (same behavior, nice cards) ---------- */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 space-y-4">
        <div className="flex items-start gap-3 mb-2">
          <div className="w-9 h-9 rounded-xl bg-emerald-50 flex items-center justify-center">
            <Leaf className="w-4 h-4 text-emerald-600" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Recommended Crops
            </h2>
            <p className="text-green-700 text-sm">
              Ranked by suitability for your soil and current conditions.
            </p>
          </div>
        </div>

        <div className="space-y-4">
          {crops.map((crop) => (
            <div
              key={crop.name}
              className="rounded-2xl border border-green-100 bg-emerald-50/40 px-5 py-4 space-y-3"
            >
              <div className="flex items-center justify-between gap-3">
                <div className="flex items-center gap-2">
                  <Leaf className="w-4 h-4 text-emerald-600" />
                  <p className="text-green-900 font-semibold">{crop.name}</p>
                </div>
                <div className="flex flex-col items-end text-right">
                  <span className="inline-flex items-center justify-center px-3 py-1 rounded-full bg-emerald-500 text-white text-xs font-semibold">
                    {crop.suitability}% Match
                  </span>
                  <span className="text-[11px] text-green-700 mt-1">
                    {crop.weatherImpact}
                  </span>
                </div>
              </div>

              <p className="text-green-800 text-sm">{crop.reason}</p>

              <div className="mt-1">
                <div className="h-2 rounded-full bg-emerald-100 overflow-hidden">
                  <div
                    className="h-full rounded-full bg-emerald-500"
                    style={{ width: `${crop.suitability}%` }}
                  />
                </div>
              </div>
            </div>
          ))}

          {crops.length === 0 && (
            <p className="text-sm text-green-700">
              No crop recommendations available for this soil type.
            </p>
          )}
        </div>
      </section>

      {/* ---------- GROWTH TIMELINE (vertical steps) ---------- */}
      <section className="bg-white rounded-3xl p-8 shadow-lg shadow-green-500/5 border border-green-100 space-y-6">
        <div className="flex items-start gap-3">
          <div className="w-9 h-9 rounded-xl bg-emerald-50 flex items-center justify-center">
            <Clock className="w-4 h-4 text-emerald-600" />
          </div>
          <div>
            <h2 className="text-lg font-semibold text-green-900">
              Growth Timeline
            </h2>
            <p className="text-green-700 text-sm">
              Step-by-step guide for planting and harvesting in your soil type.
            </p>
          </div>
        </div>

        {/* crop tabs */}
        <div className="flex flex-wrap gap-2">
          {crops.map((crop, index) => {
            const isActive = index === activeCropIndex;
            return (
              <button
                key={crop.name}
                type="button"
                onClick={() => setActiveCropIndex(index)}
                className={`px-4 py-1.5 rounded-full text-xs font-medium border ${
                  isActive
                    ? "bg-emerald-500 text-white border-emerald-500 shadow-sm"
                    : "bg-emerald-50 text-emerald-800 border-emerald-100 hover:bg-emerald-100"
                }`}
              >
                {crop.name}
              </button>
            );
          })}
        </div>

        {/* timeline body */}
        {activeCrop ? (
          <div className="mt-4 relative">
            <p className="text-sm text-green-800 mb-4">
              Follow this timeline for{" "}
              <span className="font-semibold">{activeCrop.name}</span> in{" "}
              <span className="font-semibold">{analysis.soilType}</span>.
            </p>

            <div className="relative pl-6">
              <div className="absolute left-[11px] top-0 bottom-0 w-px bg-emerald-200" />

              {activeCrop.growthTimeline.map((step, index) => (
                <div key={index} className="relative pb-6 last:pb-0">
                  <div className="absolute left-0 top-1.5 w-3 h-3 rounded-full bg-emerald-500 border-2 border-white shadow" />
                  <div className="ml-4 bg-emerald-50/60 border border-emerald-100 rounded-2xl px-4 py-3">
                    <div className="flex items-center justify-between gap-3 mb-1">
                      <p className="text-sm font-semibold text-green-900">
                        {step.stage}
                      </p>
                      {step.duration && (
                        <span className="inline-flex items-center px-2.5 py-1 rounded-full bg-white border border-emerald-100 text-[11px] font-medium text-emerald-800">
                          {step.duration}
                        </span>
                      )}
                    </div>
                    <p className="text-xs text-green-800 leading-relaxed">
                      {step.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <p className="text-sm text-green-700">
            No growth timeline available. Try analyzing another soil image.
          </p>
        )}
      </section>
    </div>
  );
}
