import React, { useState } from "react";

function App() {
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);

  // Store selected image
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
    setPreview(URL.createObjectURL(file)); // Show preview
  };

  // Send to backend
  const analyzeImage = async () => {
    if (!imageFile) return alert("Please select a soil image first.");

    const formData = new FormData();
    formData.append("image", imageFile); // field must match backend: upload.single("image")

    const res = await fetch("http://localhost:5000/api/soil/analyze-image", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    setResult(data);
  };

  return (
    <div style={{ width: 400, margin: "auto", textAlign: "center", fontFamily: "Arial" }}>
      <h2>ðŸŒ± Soil Analyzer Test</h2>

      <input 
        type="file" 
        accept="image/*" 
        onChange={handleImageUpload} 
        style={{ marginTop: 20 }}
      />

      {preview && (
        <img 
          src={preview} 
          alt="preview" 
          style={{ width: "100%", marginTop: 15, borderRadius: 10 }} 
        />
      )}

      <button 
        onClick={analyzeImage}
        style={{
          marginTop: 15,
          background: "green",
          color: "white",
          padding: "10px 20px",
          border: "none",
          borderRadius: 5,
          cursor: "pointer"
        }}
      >
        Analyze Soil
      </button>

      {result && (
        <div style={{ marginTop: 20, background: "#e8ffe8", padding: 15, borderRadius: 8 }}>
          <h3>AI Result:</h3>
          <p><b>Soil Type:</b> {result.soilType}</p>
          <p><b>Description:</b> {result.description}</p>
          <p><b>Note:</b> {result.note}</p>
        </div>
      )}
    </div>
  );
}

export default App;
